function formatDate(inputDate) {
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

    const dateParts = inputDate.split('/');
    const year = dateParts[0];
    const month = months[parseInt(dateParts[1], 10) - 1];
    const day = parseInt(dateParts[2], 10);

    const ordinalSuffix = (day) => {
        if (day >= 11 && day <= 13) {
            return 'th';
        }
        switch (day % 10) {
            case 1:
                return 'st';
            case 2:
                return 'nd';
            case 3:
                return 'rd';
            default:
                return 'th';
        }
    };

    const formattedDate = `the ${day}${ordinalSuffix(day)} of ${month}`;
    return formattedDate;
}

const deadline = new Date($('Filter').item.json.formattedDate);
deadline.setDate(deadline.getDate() - 12);
$input.item.json.uploadDeadline = formatDate(deadline.toISOString().split('T')[0].replaceAll('-', '/'));
$input.item.json.test = $('Filter').item.json.formattedDate
$input.item.json.delDate = formatDate($('Filter').item.json.formattedDate);

return $input.item;

function formatDate(inputDate) {
  // Create a Date object from the input date string
  const dateObject = new Date(inputDate);

  // Extract year, month, and day components
  const year = dateObject.getFullYear();
  const month = String(dateObject.getMonth() + 1).padStart(2, '0'); // Months are zero-based
  const day = String(dateObject.getDate()).padStart(2, '0');

  // Create the formatted date string in "yyyy-mm-dd" format
  const formattedDate = `${year}/${month}/${day}`;

  return formattedDate;
}

// Example usage:
const inputDateString = $('Shopify').item.json.tags;
const formattedDateString = formatDate(inputDateString);
$input.item.json.formattedDate = formattedDateString
return $input.item;

// Add a new field called 'myNewField' to the JSON of the item
function base64Encode(str) {
  const buffer = Buffer.from(str, 'utf-8');
  return buffer.toString('base64');
}
$input.item.json.encoded = base64Encode($('Shopify').item.json.order_number.toString())
return $input.item;

// Add a new field called 'myNewField' to the JSON of the item
$input.item.json.myNewField = 1;
const orderNumbers = $json.data.map((data) => data.order_number).join(' ')
$input.item.json.subject = `Failed to get date for orders ${orderNumbers}` 

return $input.item;
